import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
// why would we want to use a Class vs a function?
// by using a class component we have access
// to state



class App extends Component {
  constructor() {
    super();

    this.state = {
      string: 'Hello Luis, damn you learning though',
      cipher: 'Now this is kinda strange though'
    }
  }

  render() {
    return (
      <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          {this.state.cipher}
        </p>
        <button onClick={() => this.setState({ string:'Hello Kara'})}>Change Random Text</button>
      </header>
    </div>
    )
  }
}




